mapper.select:
select 

,

from tb_culpro_data
where itemID = #{itemid,jdbcType=INTEGER}
  and itemCode = #{itemcode,jdbcType=CHAR}
  and DATA_TYPE = #{dataType,jdbcType=VARCHAR}
order by itemUpdateAt desc

select

,

,
cf.FILE_PATH,
cf.FILE_NAME
from tb_culpro_data cr left join tb_culpro_file cf
on cr.itemCode = cf.DATA_CODE
where cr.itemID = #{key.itemid,jdbcType=INTEGER}
and cr.itemCode = #{key.itemcode,jdbcType=CHAR}
and cr.DATA_TYPE = #{dataType,jdbcType=VARCHAR}

mapper.delete:
delete from tb_culpro_data
where itemID = #{itemid,jdbcType=INTEGER}
  and itemCode = #{itemcode,jdbcType=CHAR}

mapper.update:
update tb_culpro_data


        DATA_DELAYED_RELEASE = #{dataDelayedRelease,jdbcType=TIMESTAMP},
        DATA_STATUS = #{dataStatus,jdbcType=VARCHAR},


where itemID = #{key.itemid,jdbcType=INTEGER}
and itemCode = #{key.itemcode,jdbcType=CHAR}
